/** Модель физического объекта */
type MeteoriteModel = {
  /** Расстояние от поверхности планеты до центра масс метеорита */
  distance: number
  /** Масса */
  mass: number
  /** Радиус */
  radius: number
  /** Угол падения */
  angel: number
  /** Начальная скорость */
  initialVelocity: number
}
